mapper.insert:

	SELECT NVL(MAX(ADDRESS_BOOK_ID) + 1, 100) as ADDRESS_BOOK_ID
	FROM ADDRESS_BOOK

	INSERT INTO ADDRESS_BOOK(
                  ADDRESS_BOOK_ID
                 ,ADDRESS_BOOK_NAME
                 ,ADDRESS_BOOK_NUMBER
                 ,ADDRESS_BOOK_EMAIL
                 ,ADDRESS_BOOK_UPDATE_DATE
                 ,ADDRESS_BOOK_COMPANY_NAME
                 ,ADDRESS_BOOK_COMPANY_NUMBER
                 ,EMPLOYEE_ID
                 ,ZIP_CODE
                 ,STREET_ADDRESS
                 ,ADDRESS_BOOK_TYPE)
    VALUES(
    			  #{addressBookId}
    			 ,#{addressBookName}
    			 ,#{addressBookNumber}
    			 ,#{addressBookEmail}
    			 ,#{addressBookUpdateDate}
    			 ,#{addressBookCompanyName}
    			 ,#{addressBookCompanyNumber}
    			 ,#{employeeId}
    			 ,#{zipCode}
    			 ,#{streetAddress}
    			 ,#{addressBookType}
    			 )
