mapper.select:
SELECT d.DRAFT_NO, d.TITLE, d.CONTENT, d.APRV_STATUS, d.REJECT_REASON, d.DRAFT_DATE, e.EMPLOYEE_NAME, d.APRV_DATE, d.FORM_ID, d.BASICS_FORM_ID, d.SUBER_NO, r.EMPLOYEE_NO, r.APRV_ROLE FROM aprv_documents d JOIN employees e ON d.EMPLOYEE_NO = e.EMPLOYEE_NO JOIN aprv_routes r ON r.DRAFT_NO = d.DRAFT_NO WHERE r.APRV_ROLE = '참조' AND r.EMPLOYEE_NO = #{employeeNo} AND d.SUBER_NO = #{suberNo}
AND e.EMPLOYEE_NAME LIKE '%' || #{employeeName} || '%'
AND d.TITLE LIKE '%' || #{title} || '%'
AND d.DRAFT_DATE = #{draftDate}
ORDER BY d.DRAFT_NO

mapper.insert:

	SELECT NVL(MAX(stamp_id), 1) + 1
