mapper.update:
	UPDATE aprv_routes
	SET stamp_id = #{stampId}
	WHERE draft_no = #{draftNo}
  	AND employee_no = #{employeeNo}

mapper.select:
SELECT MAX(aprv_order)
FROM aprv_routes
WHERE draft_no = #{draftNo}

mapper.update:
UPDATE aprv_documents
SET aprv_status = #{aprvStatus}
