mapper.insert:
SELECT NVL(MAX(work_attitude_id), 1) +1
FROM attendance_management

	INSERT INTO attendance_management (work_attitude_id
                                        , attendance_date
                                        , clock_in_time
                                        , work_attitude_type
                                        , approval_datetime
                                        , employee_no
                                        , reason
                                        , total_working_hours)
                                        values(${workAttitudeId}
                                              , ${attendanceDate}
                                              , ${clockInTime}
                                              , ${workAttitudeType}
                                              , ${approvalDatetime}
                                              , ${employeeNo}
                                              , ${reason}
                                              , ${totalWorkingHours})
