mapper.select:
SELECT *
FROM board
WHERE board_type = #{boardType}
AND suber_no =
#{suberNo}

	AND department_no = #{departmentNo}


	AND employee_no = #{employeeNo}

SELECT * FROM ( SELECT p.*, e.employee_name AS employeeName, ROW_NUMBER() OVER (ORDER BY p.post_date DESC) AS rn FROM board_post p JOIN board b ON p.board_id = b.board_id JOIN employees e ON p.employee_no = e.employee_no WHERE b.suber_no = #{suberNo} AND b.board_type = '공지'
AND ( LOWER(p.post_title) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(p.post_content) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(e.employee_name) LIKE '%' || LOWER(#{keyword}) || '%' )
) WHERE rn BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM ( SELECT p.*, e.employee_name AS employeeName, ROW_NUMBER() OVER (ORDER BY p.post_date DESC) AS rn FROM board_post p JOIN board b ON p.board_id = b.board_id JOIN employees e ON p.employee_no = e.employee_no WHERE b.suber_no = #{suberNo} AND b.board_type = '부서' AND b.department_no = #{departmentNo}
AND ( LOWER(p.post_title) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(p.post_content) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(e.employee_name) LIKE '%' || LOWER(#{keyword}) || '%' )
) WHERE rn BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM ( SELECT p.*, e.employee_name AS employeeName, ROW_NUMBER() OVER (ORDER BY p.post_date DESC) AS rn FROM board_post p JOIN board b ON p.board_id = b.board_id JOIN employees e ON p.employee_no = e.employee_no WHERE b.suber_no = #{suberNo} AND b.board_type = '자유'
AND ( LOWER(p.post_title) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(p.post_content) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(e.employee_name) LIKE '%' || LOWER(#{keyword}) || '%' )
) WHERE rn BETWEEN #{offset} + 1 AND #{offset} + #{limit}

mapper.update:
UPDATE board_post
SET views = NVL(views, 0) + 1
WHERE post_id = #{postId}
