mapper.select:
SELECT *
FROM board
WHERE board_type = #{boardType}
AND suber_no =
#{suberNo}

	AND department_no = #{departmentNo}


	AND employee_no = #{employeeNo}

SELECT * FROM ( SELECT p.*, e.employee_name AS writer, ROW_NUMBER() OVER (ORDER BY p.post_date DESC) AS rn FROM board_post p JOIN board b ON p.board_id = b.board_id JOIN employees e ON p.employee_no = e.employee_no WHERE b.suber_no = #{suberNo} AND b.board_type = '공지'
AND ( LOWER(p.post_title) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(p.post_content) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(e.employee_name) LIKE '%' || LOWER(#{keyword}) || '%' )
) WHERE rn BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM ( SELECT p.*, e.employee_name AS writer, ROW_NUMBER() OVER (ORDER BY p.post_date DESC) AS rn FROM board_post p JOIN board b ON p.board_id = b.board_id JOIN employees e ON p.employee_no = e.employee_no WHERE b.suber_no = #{suberNo} AND b.board_type = '부서' AND b.department_no = #{departmentNo}
AND ( LOWER(p.post_title) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(p.post_content) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(e.employee_name) LIKE '%' || LOWER(#{keyword}) || '%' )
) WHERE rn BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT * FROM ( SELECT p.*, e.employee_name AS writer, ROW_NUMBER() OVER (ORDER BY p.post_date DESC) AS rn FROM board_post p JOIN board b ON p.board_id = b.board_id JOIN employees e ON p.employee_no = e.employee_no WHERE b.suber_no = #{suberNo} AND b.board_type = '자유'
AND ( LOWER(p.post_title) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(p.post_content) LIKE '%' || LOWER(#{keyword}) || '%' OR LOWER(e.employee_name) LIKE '%' || LOWER(#{keyword}) || '%' )
) WHERE rn BETWEEN #{offset} + 1 AND #{offset} + #{limit}

mapper.insert:

    SELECT board_post_seq.NEXTVAL FROM dual

INSERT INTO board_post (
    post_id, board_id, employee_no, post_title, post_content, post_date,
    fixed, faq, location, read
) VALUES (
    #{postId}, #{boardId}, #{employeeNo}, #{postTitle}, #{postContent}, #{postDate},
    #{fixed}, #{faq}, #{location}, #{read}
)
