mapper.select:
SELECT * FROM ( SELECT B.POST_ID, B.PARENT_COMMENT_ID, B.POST_TITLE, B.POST_CONTENT, B.POST_DATE, B.FIXED, B.FAQ, B.LOCATION, B.READ, B.BOARD_ID, B.EMPLOYEE_NO, E.EMPLOYEE_NAME, B.child_yn, ROW_NUMBER() OVER (ORDER BY CASE WHEN B.FAQ = 'Y' THEN 0 ELSE 1 END, B.POST_ID DESC) AS rnum FROM BOARD_POST B LEFT JOIN EMPLOYEES E ON B.EMPLOYEE_NO = E.EMPLOYEE_NO WHERE B.BOARD_ID = 999 AND B.PARENT_COMMENT_ID IS NULL
AND B.EMPLOYEE_NO = #{employeeNo}
AND B.POST_TITLE LIKE '%' || #{keyword} || '%'
) WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + 10

SELECT COUNT(*) FROM BOARD_POST WHERE BOARD_ID = 999 AND PARENT_COMMENT_ID IS NULL
AND EMPLOYEE_NO = #{employeeNo}
AND POST_TITLE LIKE '%' || #{keyword} || '%'

mapper.update:
UPDATE BOARD_POST
SET child_yn = #{childYn}
WHERE post_id = #{postId}

mapper.select:
SELECT board_id
FROM board
WHERE board_type = #{type}
  AND suber_no = #{suberNo}

    AND department_no = #{departmentNo}
